.features strong {
    @apply text-gray-100;
  }

  .feature-grid {
    @apply grid my-24 col-gap-24 row-gap-12;

    i {

      @apply mb-4 text-primored text-4xl;
    }
  }

  .features h2 {
    @apply text-xl font-bold mb-1;
  }

.bottom-button {
    @apply p-2 text-center border border-red-700 rounded text-gray-100 transition-colors duration-200;
    &:hover {
      @apply bg-red-700;
    }
  }


  .on-dark {
    @apply text-gray-200;
    a {
      @apply text-gray-300 transition-colors duration-200;
      &:hover {
        @apply text-white;
      }
    }
  }

  .on-light {
    @apply text-gray-800;
    a {
      @apply text-gray-800 transition-colors duration-200;
      &:hover {
        @apply text-gray-700;
      }
    }
  }

  footer {
    @apply text-gray-300 flex flex-row flex-wrap justify-between w-full;
  }

  a {
    @apply text-gray-300 transition-colors duration-200;
    &:hover {
      @apply text-white;
    }
  }

  .footer-links {
    @apply flex flex-row justify-between;
  }

  .footer-links a {
    margin: 0 0.5rem;
  }

  .footer-links a:first-child {
    margin-left: 0;
  }

  .primo {
    font-weight: 800;
    @apply text-primored;
  }

